import { ADD_TO_CART, REMOVE_ITEM, SUB_QUANTITY, ADD_QUANTITY } from './types';

export function addToCart(data: {}) {
  return {
    type: ADD_TO_CART,
    data
  };
}

export function removeItem(id: string) {
  return {
    type: REMOVE_ITEM,
    id
  };
}

export function addQuantity(id: string) {
  return {
    type: ADD_QUANTITY,
    id
  };
}

export function subtractQuantity(id: string) {
  return {
    type: SUB_QUANTITY,
    id
  };
}
